
package Question5;

public class Employee 
{
    private int idNum;
    private double hourlyWage;
    
    public Employee() {
		// TODO Auto-generated constructor stub
	}

	public Employee(int idNum, double hourlyWage) {
		super();
		this.idNum = idNum;
		this.hourlyWage = hourlyWage;
	}

	public int getIdNum() {
		return idNum;
	}

	public double getHourlyWage() {
		return hourlyWage;
	}
	
	
    
}
